import { useAuth0 } from "@auth0/auth0-react";
import { useState, useEffect } from "react";
import axios from "axios";
import PuppiesList from "./PuppiesList";
import ApplicationList from "./ApplicationList";

const MyApplication = ({ puppyId }) => {
  const { user, isAuthenticated, getAccessTokenSilently, loginWithRedirect } = useAuth0();
  const [adoptionStatus, setAdoptionStatus] = useState("not adopted");
  const [applications, setApplications] = useState([]);
  useEffect(() => {
    const fetchAdoptionStatus = async () => {
      if (isAuthenticated) {
        try {
          const accessToken = await getAccessTokenSilently();
          const response = await axios.get(`${process.env.REACT_APP_API_URL}/myapplications`, {
            headers: {
              Authorization: `Bearer ${accessToken}`,
            },
          });
          setApplications(response.data);
          // const applicationForCurrentPuppy = response.data.find(app => app.puppyId === puppyId);
          //
          // if (applicationForCurrentPuppy) {
          //   setAdoptionStatus(applicationForCurrentPuppy.status);
          // } else {
          //   setAdoptionStatus("not adopted");
          // }
        } catch (error) {
          console.error(error);
        }
      }
    };

    fetchAdoptionStatus();
  }, [isAuthenticated, puppyId, getAccessTokenSilently, user]);

  const handleAdopt = async () => {
    if (isAuthenticated) {
      try {
        const accessToken = await getAccessTokenSilently();
        await axios.post(`${process.env.REACT_APP_API_URL}/adoption-applications`, {
          puppyId: puppyId
        }, {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        });

        setAdoptionStatus("pending");
      } catch (error) {
        console.error(error);
      }
    } else {
      loginWithRedirect();
    }
  };

  const handleCancel = async () => {
    if (isAuthenticated) {
      try {
        const accessToken = await getAccessTokenSilently();
        await axios.delete(`${process.env.REACT_APP_API_URL}/adoption-applications/${puppyId}`, {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        });

        setAdoptionStatus("not adopted");
      } catch (error) {
        console.error(error);
      }
    }
  };

  // if (adoptionStatus === "not adopted") {
  //   return (
  //     <button onClick={handleAdopt}>
  //       Apply for Adoption
  //     </button>
  //   );
  // } else if (adoptionStatus === "pending") {
  //   return (
  //     <button onClick={handleCancel}>
  //       Cancel Application
  //     </button>
  //   );
  // }

  return (
    <div className={'container'}>
      {applications.length === 0 && (
        <h2>No Applications Yet</h2>
      )}
      {applications.length > 0 && (
        <div>
          <ApplicationList applications={applications}/>
        </div>
      )}

    </div>
  );
};

export default MyApplication;
